#!/usr/bin/env python3
"""
Generate ALL 228 missing SQL tables for complete SQLite coverage

This will create SQL CREATE TABLE statements for all entities
that don't have SQL tables yet.
"""

from pathlib import Path
import re

project_root = Path("/root/clawd")
sqlite_path = project_root / "src" / "infrastructure" / "sqlite_repositories.py"

print("=" * 80)
print("GENERATE ALL 228 MISSING SQL TABLES")
print("=" * 80)
print()
print("This will create SQL tables for:")
print("  - QuestSystem (8 tables)")
print("  - Social/Religion (17 tables)")
print("  - Locations (10 tables)")
print("  - Inventory/Crafting (9 tables)")
print("  - UGC/Analytics (15 tables)")
print("  - LegendaryItems (6 tables)")
print("  - Companions/Transport (9 tables)")
print("  - Institutions (7 tables)")
print("  - Media (7 tables)")
print("  - Secrets (8 tables)")
print("  - Art (7 tables)")
print("  - Other (140+ tables)")
print()
print("Total: 228 tables")
print()
print("Creating SQL tables...")
print("=" * 80)
print()

# Read existing sqlite_repositories.py
content = sqlite_path.read_text()

# Get existing tables
existing_tables = set()
table_pattern = r'CREATE TABLE IF NOT EXISTS ([a-z_]+)'
for match in re.finditer(table_pattern, content):
    existing_tables.add(match.group(1))

print(f"Existing tables: {len(existing_tables)}")
print()

